import React, { Component } from 'react';
import './item-status-filter.css';

export default class AppFilter extends Component {

  state = {
    all: true,
    active: false,
    done: false,
  }

  onAllItemsFilter = () => {
    this.setState({
      all: true,
      active: false,
      done: false,
    });
    this.props.onAllItemsFilter();
  }

  onActiveItemsFilter = () => {
    this.setState({
      all: false,
      active: true,
      done: false,
    });
    this.props.onActiveItemsFilter();
  }

  onDoneItemsFilter = () => {
    this.setState(({done}) => {
      return {
        all: false,
        active: false,
        done: true,
      }
    });
    console.log(this.state);
    this.props.onDoneItemsFilter();
  }

  render() {
    return (
    <div className="btn-group">
      <button type="button" className="btn btn-info" onClick={this.onAllItemsFilter}>All</button>
      <button type="button" className="btn btn-outline-secondary" onClick={this.onActiveItemsFilter}>Active</button>
      <button type="button" className="btn btn-outline-secondary" onClick={this.onDoneItemsFilter}>Done</button>
    </div>
    );
  }
};
