import React, { Component } from 'react';
import './item-status-filter.css';

export default class AppFilter extends Component {

  buttons = [
    { name: 'all', label: 'All' },
    { name: 'active', label: 'Active' },
    { name: 'done', label: 'Done' },
  ];

  allButtonClasses = 'btn btn-info';
  activeButtonClasses = 'btn btn-outline-secondary';
  doneButtonClasses = 'btn btn-outline-secondary';

  onFilter = (e) => {
    //this.changeClass('btn btn-info', 'btn btn-outline-secondary', 'btn btn-outline-secondary');
    this.props.onFilter(e.target.innerText.toLowerCase());
  }



  render() {
    const buttons = this.buttons.map(({name, label}) => {
    return <button type="button" className={this.allButtonClasses} onClick={this.onFilter}>{label}</button>
    });
    return (
    <div className="btn-group">
     {buttons}
    </div>
    );
  }
};
