import React, { Component } from 'react';
import './add-item.css';

export default class AddItem extends Component {



  render() {
    return (
    <div className="form-row add-item">
      <div className="col-7">
      <input type="text" className="form-control" placeholder="type new task" aria-label="Recipient's username" aria-describedby="button-addon2" onInput={this.onAddItemClick}/>
      </div>
      <div className="col-auto my-1">
        <button className="btn btn-success" type="button" id="button-addon2"
        onClick={() => this.props.onAdded('lalala')}
        >Add</button>
      </div>
    </div>
    );
  }
};
