import React, { Component } from 'react';
import './add-item.css';

export default class AddItem extends Component {

  state = {
    disabled: true,
    newItem: '',
  };

  onAddItemClick = () => {

    this.setState(({disabled, newItem}) => {
      return {
        disabled: !disabled,
      }
    });
  };

  render() {
    return (
    // <div className="input-group mb-3 add-item">
