import React, { Component } from 'react';
import './item-status-filter.css';

export default class AppFilter extends Component {
  classNameInfo = 'btn btn-info';
  classNameSecondary = 'btn btn-outline-secondary';

  buttons = [
    { name: 'all', label: 'All' },
    { name: 'active', label: 'Active' },
    { name: 'done', label: 'Done' },
  ];
  onFilter = (e) => {
    this.props.onFilter(e.target.innerText.toLowerCase());
  }

  render() {
    const { filter } = this.props;

    const buttons = this.buttons.map(({name, label}) => {
      if(label === filter) {
        return <button type="button" className="btn btn-outline-secondary" key={name} onClick={this.onFilter}>{label}</button>
      }

    });

    return (
    <div className="btn-group">
     {buttons}
    </div>
    );
  }
};
