import React, { Component } from 'react';
import ReactDOM from 'react-dom';

import AppHeader from '../app-header';
import SearchPanel from '../search-panel';
import TodoList from '../todo-list/todo-list';
import AppFilter from '../item-status-filter';
import AddItem from '../add-item';
import './app.css';

export default class App extends Component {
  randomIndex = 100;

  state = {
    todoData: [
      {label: 'Drink milk', important: false, id: 1},
      {label: 'Drink tea', important: false, id: 2},
      {label: 'Learn React', important: true, id: 3},
    ]
  };

  deleteItem = (id) => {
    this.setState(({todoData}) => {
      const result = todoData.filter(el => el.id !== id);
      return {
        todoData: result,
      }

    })
  };

  addItem = (text) => {
    this.setState(() => {
      const newElement ={
        label: text,
        important: false,
        id: this.randomIndex++,
      };
      const newArr = {...this.state.todoData, newElement};
      return newArr;
    });
  };

  render() {
    return (
      <div className="todo-app">
        <AppHeader toDo={1} done={3} />
        <div className="top-panel d-flex">
          <SearchPanel />
          <AppFilter />
        </div>

        <TodoList todos={this.state.todoData}
        onDeleted={(id) => this.deleteItem(id)} />
        <AddItem onAdded ={(text) => this.addItem(text)}/>
      </div>
    );
  }
};

ReactDOM.render(<App />, 
  document.getElementById('root'));
