import React from 'react';
import './todo-list-item.css';

const TodoListItem = ( { label, important = false }  ) => {
  const style = {
    color: important ? 'tomato' : 'black'
  };
  return (
    <span className="todo-list-item">
      <span className="todo-list-item-label"
          style={style}>
          { label }
      </span>
      <button type="button" class="btn btn-outline-danger">
      <svg class="bi bi-exclamation" width="32" height="32" viewBox="0 0 20 20" fill="currentColor" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M6.646 3.646a.5.5 0 01.708 0l6 6a.5.5 0 010 .708l-6 6a.5.5 0 01-.708-.708L12.293 10 6.646 4.354a.5.5 0 010-.708z"/></svg>

      </button>
    </span>

  );
};

export default TodoListItem;
