import React, { Component } from 'react';
import ReactDOM from 'react-dom';

import AppHeader from '../app-header';
import SearchPanel from '../search-panel';
import TodoList from '../todo-list/todo-list';
import AppFilter from '../item-status-filter';
import AddItem from '../add-item';
import './app.css';

export default class App extends Component {
  randomIndex = 100;

  state = {
    todoData: [
      this.createTodoItem('Drink milk'),
      this.createTodoItem('Drink tea'),
      this.createTodoItem('Learn React'),
    ]
  };

  createTodoItem(label) {
    return {
        label: label,
        important: false,
        done: false,
        id: this.randomIndex++,
    }
  }

  toggleProperty(arr, id, propName) {
    const newArr = arr.filter(el => {
      if (el.id === id) {
        el[propName] = !el[propName]
      }
      console.log(el);
      return el;
    });
    console.log(newArr)
    return newArr;
  };

  // toggleProperty(arr, id, propName) {
  //   let newArr = 
  //     arr.map(el => {
  //     if (el.id === id) {
  //       el[propName] = !el[propName]
  //     }
  //     return el;
  //   });
  //   return newArr;
  // };

  deleteItem = (id) => {
    this.setState(({todoData}) => {
      const result = todoData.filter(el => el.id !== id);
      return {
        todoData: result,
      };
    });
  };

  addItem = (text) => {
      const newElement = this.createTodoItem(text);
      this.setState(({todoData}) => {
      let newArr = [...todoData, newElement];
      console.log(newArr);

      return {
        todoData: newArr,
      };
    });
  };

  // onToggleImportant = (id) => {
  //   this.setState(({todoData}) => {
  //     return {
  //       todoData:
  //         this.toggleProperty(todoData, id, 'important'),
  //     }
  //   })
  // };
  onToggleImportant = (id) =>  {
    this.setState(({todoData}) => {
    const newArr = todoData.map(item => {
      if ( item.id === id ) { 
        // return { 
        //   ...item,
        //   'important': !item['important']
        //  }
        item['important'] = !item['important'];
      } 
       return item 
    });
    console.log(newArr);
    return {
      todoData: newArr,
    }
    });
    };

  onToggleDone = (id) => {
    this.setState(({todoData}) => {
      const arr = this.toggleProperty(todoData, id, 'done');
      console.log(arr);
      return {
        todoData: arr,
      }
    })
  };

  render() {
    return (
      <div className="todo-app">
        <AppHeader toDo={1} done={3} />
        <div className="top-panel d-flex">
          <SearchPanel />
          <AppFilter />
        </div>

        <TodoList todos={this.state.todoData}
        onDeleted={(id) => this.deleteItem(id)}
        onToggleImportant={this.onToggleImportant}
        onToggleDone={this.onToggleDone}
        />
        <AddItem onAdded ={(text) => this.addItem(text)}/>
      </div>
    );
  }
};

ReactDOM.render(<App />, 
  document.getElementById('root'));
