import React, { Component } from 'react';
import './item-status-filter.css';

export default class AppFilter extends Component {

  allButtonClasses = 'btn btn-info';
  activeButtonClasses = 'btn btn-outline-secondary';
  doneButtonClasses = 'btn btn-outline-secondary';

  changeClass = (allClass, activeClass, doneClass) => {
    this.allButtonClasses = allClass;
    this.activeButtonClasses = activeClass;
    this.doneButtonClasses = doneClass;
  }

  onFilter = (e) => {
    this.changeClass('btn btn-info', 'btn btn-outline-secondary', 'btn btn-outline-secondary');
    this.props.onAllItemsFilter(e.target.innerText.toLowerCase());
  }

  onActiveItemsFilter = (e) => {
    this.changeClass('btn btn-outline-secondary', 'btn btn-info', 'btn btn-outline-secondary');
    this.props.onActiveItemsFilter(e.target.innerText.toLowerCase());
  }

  onDoneItemsFilter = (e) => {
    this.changeClass('btn btn-outline-secondary', 'btn btn-outline-secondary', 'btn btn-info');
    this.props.onDoneItemsFilter(e.target.innerText.toLowerCase());
  }

  render() {
    return (
    <div className="btn-group">
      <button type="button" className={this.allButtonClasses} onClick={this.onFilter}>All</button>
      <button type="button" className={this.activeButtonClasses} onClick={this.onFilter}>Active</button>
      <button type="button" className={this.doneButtonClasses} onClick={this.onFilter}>Done</button>
    </div>
    );
  }
};
