  render() {
    return (
    <div className="btn-group">
      <button type="button" className="btn btn-info" onClick={this.props.onAllItemsFilter}>All</button>
      <button type="button" className="btn btn-outline-secondary" onClick={this.props.onActiveItemsFilter}>Active</button>
      <button type="button" className="btn btn-outline-secondary" onClick={this.props.onDoneItemsFilter}>Done</button>
    </div>
    );
  }
};
