import './add-item.css';

export default class AddItem extends Component {

  state = {
    disabled: true,
    newItem: '',
  };

  onAddItemClick = () => {

    this.setState(({disabled}) => {
      return {
        done: !done,
      }
    });
  };

  render() {
    return (
    <div className="input-group mb-3 add-item">
      <input type="text" className="form-control" placeholder="type new task" aria-label="Recipient's username" aria-describedby="button-addon2" onChange={this.onAddItemClick}/>
      <div className="input-group-append">
        <button className="btn btn-success" disabled type="button" id="button-addon2">Add</button>
      </div>
    </div>
    );
  }
};
