import React, { Component } from 'react';
import ReactDOM from 'react-dom';

import AppHeader from '../app-header';
import SearchPanel from '../search-panel';
import TodoList from '../todo-list/todo-list';
import AppFilter from '../item-status-filter';
import AddItem from '../add-item';
import './app.css';

export default class App extends Component {
  randomIndex = 100;

  state = {
    todoData: [
      this.createTodoItem('Drink milk'),
      this.createTodoItem('Drink tea'),
      this.createTodoItem('Learn React'),
    ]
  };

  createTodoItem(label) {
    return {
        label: label,
        important: false,
        done: false,
        id: this.randomIndex++,
    }
  }

  deleteItem = (id) => {
    this.setState(({todoData}) => {
      const result = todoData.filter(el => el.id !== id);
      return {
        todoData: result,
      };
    });
  };

  addItem = (text) => {
      const newElement = this.createTodoItem(text);
      this.setState(({todoData}) => {
      let newArr = [...todoData, newElement];
      console.log(newArr);

      return {
        todoData: newArr,
      };
    });
  };

  onToggleImportant = (id) => {
    this.setState(({todoData}) => {
      return {
        todoData:
          this.toggleProperty(todoData, id, 'important')
      }
    })
  };

  onToggleDone = (id) => {
    this.setState(({todoData}) => {
      return {
        todoData:
          this.toggleProperty(todoData, id, 'done')
      }
    })
  };

  render() {
    return (
      <div className="todo-app">
        <AppHeader toDo={1} done={3} />
        <div className="top-panel d-flex">
          <SearchPanel />
          <AppFilter />
        </div>

        <TodoList todos={this.state.todoData}
        onDeleted={(id) => this.deleteItem(id)}
        onToggleImportant={this.onToggleImportant}
        onToggleDone={this.onToggleDone}
        />
        <AddItem onAdded ={(text) => this.addItem(text)}/>
      </div>
    );
  }
};

ReactDOM.render(<App />, 
  document.getElementById('root'));
